const humidity = +msg.payload
const reference = +global.get('homeassistant').homeAssistant.states['sensor.0xbc33acfffe26dc80_humidity'].state

if (!humidity) {
    return {}
}

const payload = humidity > (reference + 10)
    ? 'on'
    : 'off'

return {
    payload,
    data: {
        humidity,
        reference
    }
}
